string kthSmallestPath_(int left, int k, vector<vector<int>>& comTable, const int& tot) {

	if (left == 0) return string(tot, 'H');

	int pos = tot - left;

	string res;

	for (; pos >= 0; --pos) {

		if (comTable[tot - pos - 1][left - 1] >= k) {
			res = kthSmallestPath_(left - 1, k, comTable, tot);
			break;
		}

		k -= comTable[tot - pos - 1][left - 1];

	}

	res[pos] = 'V';

	return res;
}

string kthSmallestPath(vector<int>& destination, int k) {

	int tot = destination[0] + destination[1];
	vector<vector<int>> comTable(tot + 1, vector<int>(tot + 1, 0));


	for (int i = 0; i <= tot; ++i) {
		comTable[i][0] = 1;
		comTable[i][i] = 1;
		for (int j = 1; j < i; ++j) {
			comTable[i][j] = comTable[i - 1][j - 1] + comTable[i - 1][j];
		}
	}

	return kthSmallestPath_(destination[0], k, comTable, tot);

}
