#define ll int
#define pb push_back
ll dx[2]={0, 1};
ll dy[2]={1, 0};
class Solution {
public:

    void  update(string str, ll sx, ll sy, ll ex, ll ey,vector<string>& arr , vector<vector<bool>> vis)
    {


        if(sx==ex && sy==ey)
        {
            arr.pb(str);
            return ;

        }
        vis[sx][sy]=1;
        for(ll i=0;i<2;i++)
        {

            ll nx=sx+dx[i];
            ll ny=sy+dy[i];

            if(nx>ex || ny>ey || vis[nx][ny])
                continue;

            if(i==0)
            {
              update(str+"H", nx, ny, ex, ey, arr, vis);  
            }
            else
            {
               update(str+"V", nx, ny, ex, ey, arr, vis); 

            }


        }




    }
    string kthSmallestPath(vector<int>& destination, int k) {

        ll ex=destination[0];
        ll ey=destination[1];

        ll sx=0, sy=0;

        vector<string> arr;

        string str="";

        vector<vector<bool>>vis(ex, vector<bool>(ey, 0));

        update(str, sx, sy, ex, ey, arr, vis);



        sort(arr.begin(), arr.end());

        return arr[k-1];



    }
};

