public int[] shortestToChar(String s, char c) {

    int ptr1 = Integer.MAX_VALUE;
    int ptr2 = Integer.MAX_VALUE;
    int index = -1;
    int l = s.length();
    int[] ans = new int[l];

    while(index<l-1){
        int temp = index+1;

        while(temp<l && s.charAt(temp)!=c)
            temp++;

        if(temp == l){
            temp = Integer.MAX_VALUE;
        }

        ptr2 = ptr1;
        ptr1 = temp;

        index++;
        while(index<l && index<=temp){
            ans[index] = Math.min(Math.abs(ptr1-index),Math.abs(ptr2-index));
            index++;
        }
        index--;
    }

    return ans;
}

