class Solution 
{
    public int[] shortestToChar(String s, char c) 
    {
        int l = -1;
        int r = s.indexOf(c);
        int[] ans = new int[s.length()];

        for(int i = 0 ; i < s.length() ; i++)
        {
            if(s.charAt(i) == c )
            {
                l = r;

                if(s.substring(i+1).indexOf(c) == -1)
                    r = -1;
                else
                    r = (i+1) + s.substring(i+1).indexOf(c);
            }

            if(r == -1)
                ans[i] = i-l;
            else if(l == -1)
                ans[i] = r-i;
            else if(r != -1 && l != -1)
                ans[i] = Math.min(i - l , r - i);
        }

        return ans;
    }
}

