class Solution:
    def shortestToChar(self, s: str, c: str) -> List[int]:
        n=len(s)
        arr=[i for i in range(n) if s[i]==c]
        arr1=[]
        j=0
        for i in range(n):
            if s[i]==c:
                arr1.append(0)
                j=j+1
            elif i<arr[0]:
                arr1.append(arr[0]-i)
