class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        vector<int> ans;
        int n = s.size();
        for(int i=0;i<n;++i){
            if(s[i]==c){
                ans.push_back(0);
            }
            else{
                int st = i;
                int en = i;
                bool find1=false,find2=false;
                while(st>=0){
                    if(s[st]==c){
                        find1 = true;
                        break;
                    }
                    st--;
                }
                while(en<n){
                    if(s[en]==c){
                        find2 = true;
                        break;
                    }
                    en++;
                }
                if(find1 && find2){
                    ans.push_back(min(en-i,i-st));                    
                }else if(find1){
                    ans.push_back(i-st);
                }else{
                    ans.push_back(en-i);
                }
            }
        }
        return ans;
    }
};
