class Solution {

    public int closest(int elem, List<Integer> list)
    {
        if(elem<=list.get(0))
        {
            return Math.abs(list.get(0)-elem);
        }
        int min = Integer.MAX_VALUE;
        for(int i=0;i<list.size();i++)
        {
            int diff = Math.abs(list.get(i)-elem);
            if(min>diff)
            {
                min = diff;
            }
        }
        return min;
