        for(int i=0;i<s.length();i++)
        {
            if(s.charAt(i)==c)
                list.add(i);
        }

        for(int i=0;i<s.length();i++)
        {
            if(s.charAt(i)==c)
                res[i] = 0;
            else
            {
                // finding the nearest distance
                int k = closest(i, list);
                res[i] = k;
            }
        }
        return res;
    }
