impl Solution {
    pub fn shortest_to_char(s: String, c: char) -> Vec<i32> {
        (0..s.len()).map(|i| {
            let left = s[0..i+1].chars().rev().position(|k| k == c).unwrap_or(99999usize);
            let right = s[i..].find(c).unwrap_or(99999usize);
            (if left < right {left} else {right}) as i32
        })
        .collect()
    }
}
