class Solution {
public:
    vector<int> shortestToChar(string S, char c) {
        vector<int> v;
        auto f = find(S.begin(),S.end(),c);
        auto i = S.begin();
        while(i!=f+1 && i!=S.end())
        {
            v.push_back(abs(i-f));
            i++;
        }
        auto fe = find(f+1,S.end(),c);
        while(i!=S.end())
        {
            if(*i==c)
            {
                v.push_back(0);
                f = i;
                fe = find(f+1,S.end(),c);
                i++;
            }
            else
            {
                if(fe!=S.end())
                    v.push_back(min(abs(fe-i),abs(f-i)));
                else
                    v.push_back(abs(f-i));
                i++;
            }
        }
        return v;
    }
};

