Just keep a check on places of occurences of 'C' and greedily choose the shortest (to left or right)
```
class Solution {
public:
    vector<int> shortestToChar(string S, char C) {
        vector<int> es;

        if(S.size()==1)
        {
            return {0};
        }

        for(int i=0;i<S.size();i++)
        {
            if(S[i]==C)
            {
                es.push_back(i);
            }
        }

        vector<int> ans(S.size());
        for(int i=0;i<ans.size();i++)
        {
            if(es.size()>1 && abs(i-*es.begin())>abs(i-*(es.begin()+1)))
            {
                es.erase(es.begin());
            }
            ans[i]=abs(i-*es.begin());
        }

        return ans;
    }
};
