class Solution(object):
    def shortestToChar(self, S, C):
        """
        :type S: str
        :type C: str
        :rtype: List[int]
        """
        indc, dis = float('-inf'), [0] * len(S)
        for i in range(len(S)):
            if S[i] == C:
                indc = i
            dis[i] = i - indc

        indc = float('inf')
        for j in range(len(dis))[::-1]:
            if S[j] == C:
                indc = j
            dis[j] = min(dis[j], indc-j)

        return dis
