class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        int i, j;
        vector<int> ans;
        vector<int> pos;
        for(i=0 ; i<s.length() ; i++)
        {
            if(s[i]==c)
            {
                pos.push_back(i);
            }
        }
        j=0;
        for(i=0 ; i<s.length() ; i++)
        {
            if(j==pos.size()-1)
            {
                ans.push_back(abs(i-pos[j]));
            }
            else if(abs(i-pos[j])>abs(i-pos[j+1]))
            {
                j++;
                ans.push_back(abs(i-pos[j]));
            }
            else
            {
                ans.push_back(abs(i-pos[j]));
            }
        }
        return ans;
    }
};
