class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        int n = s.size();
        vector<int> ans(n, -1);

        // from right to left, maintain lastOccurence of c and update ans[i] accordingly.
        int lastOcc = -1;
        for(int i=n-1;i>=0;i--){
            if(s[i] == c){
                lastOcc = i;
                ans[i] = 0;
            } 
            else{
                ans[i] = abs(lastOcc - i);
            }
        }

         // from left to right, maintain firstOccurence of c and update ans[i] accordingly if found minimum.
        int firstOcc = lastOcc;
        for(int i=0;i<n;i++){
            if(s[i] == c){
                firstOcc = i;
                ans[i] = 0;
            }
            else{
                ans[i] = min(ans[i], abs(firstOcc - i));
            }
        }

        return ans;
    }
};
