            lastOccurence = i
        }
    }

    for i := 0; i < len(s); i++ {
        if s[i] == c {
            res = append(res, 0)
            firstOccurennce = i
        } else {
            res = append(res, abs(i - firstOccurennce))
        }
    }

    for i := len(s) - 1; i > -1; i-- {
        if s[i] == c {
            lastOccurence = i
        } else {
            res[i] = min(res[i], abs(i - lastOccurence))
        }
    }

    return res
}

func abs(a int) int {
    if a >= 0 {
        return a
    }

    return -a
}

func min(a, b int) int {
    if a < b {
        return a
    }

    return b
}
