var shortestToChar = function(s, c) {
   s = s.split('')
  let position = []
  for(let i = 0; i < s.length; i++) {
      let letter = s[i]
      if(letter === c) {
          position.push(i)
      }
  }  

  for(let i = 0; i < s.length; i++) {
      let letter = s[i]
      if(letter === c) {
          s[i] = 0
      } else {
          let min = Infinity
          for(let j = 0; j < position.length; j++) {
              let num = position[j]
              if(Math.abs(num - i) < min) {
                  min = Math.abs(num - i)
              }
          }
          s[i] = min
      }
  }

  return s

};
