class Solution {
public:
    vector<int> shortestToChar(string s, char c) 
    {
        vector<int> answer(s.length());
        for(int i=0;i<s.length();i++)
        {
            if(s[i]==c)
                answer[i]=0;
            else
            {
                //left side
                int j=i-1;
                int left=0;
                bool leftflag=false;

                while(j>=0)
                {
                    if(s[j]==c)
                    {
                        leftflag=true;
                        left++;
                        break;
                    }
                    left++;
                    j--;
                }
                //right side
                bool rightflag=false;
                int k=i+1;
                int right=0;
                while(k<s.length())
                {
                    if(s[k]==c)
                    {
                        rightflag=true;
                        right++;
                        break;
                    }
                    right++;
                    k++;
                }
                if(leftflag==true && rightflag==true)
                    answer[i]=min(left,right);
                else if(leftflag==false && rightflag==true)
                {
                    answer[i]=right;
                }
                else if(rightflag==false && leftflag==true)
                {
                    answer[i]=left;
                }
            }
        }
        return answer;

    }
};

