class Solution {
    public int[] shortestToChar(String s, char c) {

        int[] res = new int[s.length()];
        Arrays.fill(res, Integer.MAX_VALUE); // all the distances are MAX for initilization
        int pos = -1; // if we haven't seen c yet, mark it as -1

        // after each c, update distance
        char[] chars = s.toCharArray();
        for (int i = 0; i < chars.length; i++) {
            if (chars[i] == c) {
                pos = i;
                res[i] = 0;
            }
            else if (pos != -1 && chars[i] != c) {
                res[i] = Math.abs(i - pos);
            }
        }
        // before each c, update distance
        for (int i = chars.length - 1; i >= 0; i--) {
            if (chars[i] == c) {
                pos = i;
            }
            else {
                res[i] = Math.min(res[i], Math.abs(i - pos));
            }
        }
        return res;
    }
}

