class Solution {
    public int[] shortestToChar(String s, char c) {
        int res[] = new int[s.length()];
        int idx = -1;

        //left scan 
        for(int i = 0;i< s.length();i++){
            if(s.charAt(i) == c)idx = i;
            if(idx == -1)res[i] = Integer.MAX_VALUE;
            else res[i] = Math.abs(idx - i);
        }

        //right scan
        idx = -1;
        for(int i = s.length() - 1;i>=0;i--){
            if(s.charAt(i) == c)idx = i;
            if(idx != -1)res[i] = Math.min(res[i],Math.abs(i - idx));
        }

        return res;
    }
}
