 vector<int> shortestToChar(string s, char c) {
        vector<int> vec;
        vec.push_back(INT_MIN);
        for(int i=0;i<s.size();i++){
            if(s[i]==c){
                vec.push_back(i);
            }
        }
        vec.push_back(INT_MAX);

        if(vec.size()==2){
            return {};
        }
        else{
            vector<int> nums;
            for(int i=0;i<s.size();i++){
                if(s[i]==c){
                    nums.push_back(0);
                    continue;
                }
                auto x=lower_bound(vec.begin(),vec.end(),i);
                int lb=x-vec.begin();
                lb--;
                auto y=upper_bound(vec.begin(),vec.end(),i);
                int ub=y-vec.begin();
                if(vec[lb]==INT_MIN){
                    nums.push_back(min(*x-0,abs(vec[ub]-i)));
                }
                else if(vec[ub]==INT_MAX){
                    nums.push_back(abs(i-vec[lb]));
                }
                else{
                    nums.push_back(min(abs(i-vec[lb]),abs(vec[ub]-i)));
                }
            }
            return nums;   
        }
    }
