class Solution {
    public int[] shortestToChar(String s, char c) 
    {
        char chr[]=s.toCharArray();
        int len=s.length();
        int ans[]=new int[len];

        for(int i=0; i<len; i++)
        {
            if(chr[i]==c)
            {
                ans[i]=0;
                continue;
            }
            int shortestDist=Integer.MAX_VALUE;
            for(int j=0; j<len; j++)
            {
                if(chr[j]==c)
                {
                    int dist = Math.abs(i-j);
                    if(dist < shortestDist)
                        shortestDist=dist;
                    else if(dist > shortestDist)
                        break;
                }
            }
            ans[i]=shortestDist;
        }

        return ans;
    }
}
// By Arindam Ghosh
