//Approach-1 (Using map)
class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        map<char, vector<int>> mp;
        int n = s.length();

        for(int i = 0; i<n; i++) {
            if(s[i] == c)
                mp[c].push_back(i); //store all indices of char c
        }



        vector<int> result(n, 0);

        for(int i = 0; i<n; i++) {
            if(s[i] != c) {
                int dis = INT_MAX;
                for(int &idx : mp[c]) {
                    dis = min(dis, abs(i-idx)); //find the best index which gives min distance
                }
                result[i] = dis;
            }
        }

        return result;
    }
};
