class Solution:
    def shortestToChar(self, s: str, c: str) -> List[int]:
        # create answer list
        length = len(s)
        ans = [length] * length

        # work left to right        
        for i in range(length):
            if s[i]==c:
                ans[i]=0
            elif i > 0:
                ans[i]=ans[i-1] + 1

        # work right to left (using negative indexing)
        for i in range(1, length+1):
            if i > 1:
                ans[-i]=min(ans[-i+1] + 1, ans[-i])

        return ans
