class Solution
{
public:
    vector<int> shortestToChar(string s, char c)
    {
        int n = s.size();
        int lastPosC = -n;
        vector<int> ans(n, n);
        for (int i = 0; i < n; i++)
        {
            if (s[i] == c)
            {
                lastPosC = i;
            }
            ans[i] = i - lastPosC;
        }
        for (int i = lastPosC - 1; i >= 0; i--)
        {
            if (s[i] == c)
            {
                lastPosC = i;
            }
            ans[i] = min(lastPosC - i, ans[i]);
        }
        return ans;
    }
};
