class Solution
{
public:
    vector<int> shortestToChar(string s, char c)
    {
        int n = s.size();
        vector<int> ans(n);
        for (int i = 0; i < n; i++)
        {
            ans[i] = s[i] == c ? 0 : n;
        }
        for (int i = 0; i < n; i++)
        {
            ans[i] = min(ans[i], ans[i - 1] + 1);
        }
        for (int i = n - 2; i >= 0; i--)
        {
            ans[i] = min(ans[i], ans[i + 1] + 1);
        }
        return ans;
    }
};
