class Solution {
public:
    string reverseParentheses(string s) {
        //Stack to hold all the strings when '(' comes;
        stack<string> st;
        //intially assume current string empty
        string curr_s = "";
        for(int i = 0;i<s.size();i++)
        {
            //if somewhere u find '(' store curr_s into stack
            if(s[i] == '(')
            {
                st.push(curr_s);
                curr_s = "";
            }
            //as brackets are closed reverse the curr_s and concatenate with stack top value
            else if(s[i] == ')')
            {
                reverse(curr_s.begin(), curr_s.end());
                string top = st.top();
                st.pop();
                top.append(curr_s);
                curr_s = top;
            }
            else
                curr_s.push_back(s[i]);
        }
        //return the curr_s
        return curr_s;

    }
};
