var reverseParentheses = function(s) {
    let stack = [[]];
    for (let i = 0; i < s.length; i++) {
        if (s[i] === '(') stack.push([]);    
        else if (s[i] === ')') {
            let cur = stack.pop().reverse();
            stack[stack.length-1].push(...cur);
        }
        else stack[stack.length-1].push(s[i]);    
    }
    return stack[0].join("");
    // Time Complexity: O(n)
    // Space Complexity: O(n)
};
