class Solution {
    public String reverseParentheses(String s) {
        Stack<Character> stack = new Stack<Character>();
        for(char c : s.toCharArray()) {
            // Find an inner word between two ( ) needs to be reversed.
            if (c == ')') {
                StringBuilder sb = new StringBuilder();
				// Reverse it in a StringBuilder until you reach (
                while(stack.peek() != '(') {
                    sb.append(stack.pop());
                }
                // Pop ( we donot needed it anymore
                stack.pop();
                // Push the reversed word again inside the stack.
                for(char insideC : sb.toString().toCharArray()){
                    stack.push(insideC);
                }
            } else {
                // Any character simply push to Stack.
                stack.push(c);
            }
        }
        // Here I'm done but my word is inside the stack, needs one more reverse. 
        StringBuilder result = new StringBuilder();
        while(!stack.isEmpty()) {
            result.insert(0, stack.pop());
        }
        return result.toString();
    }
}
