public:
    string reverseParentheses(string s) {

	   // initalize a stack

        stack<int>st;
        int i;
        int n = s.size();
	// the main logic is that every time we encounter '(' open bracket we will store it index in the stack
	// when we encounter ')' close bracket then we will reverse the string from st.top()+1 to current index 'i'.

        for(i=0;i<n;i++)
        {
            if(s[i] == '(')
            {
                st.push(i);
            }
            else if(s[i] == ')')
            {
                int start = st.top() + 1;
                int end = i;
				// will reverse the string  and pop the top index stored in stack.
                reverse(s.begin()+start,s.begin()+end);
                st.pop();
            }
        }
		// at last we will initalize a new string and store all characters except open and close bracket and will return the final string.
        string v = "";
        for(i=0;i<n;i++)
        {
            if(s[i] == '(' || s[i] == ')')
            {
                continue;
            }
            v.push_back(s[i]);
        }
        return v;

    }
};

