class Solution {
    public String reverseParentheses(String s) {
        Deque<Character> stack = new LinkedList<>();
        Queue<Character> reverseHelper = new LinkedList<>();
        for(char ch: s.toCharArray()) {
            if(ch == ')') {
                reverse(stack,reverseHelper);
            } else {
                stack.push(ch);
            }
        }

        StringBuilder sb = new StringBuilder();
		/*
		At this point, we use stack as a deque to avoid extra reverse of stack content
		Keep popping from the rear of deque
		*/
        Deque<Character> deque = stack;

        while(deque.size() > 0) {
            sb.append(deque.pollLast());
        }

        return sb.toString();
    }

	/*
	Push everything to queue and push it back to stack
	*/
    void reverse(Deque<Character> st, Queue<Character> q) {
        while(st.peek() != '(') {
            q.offer(st.pop());
        }
        st.pop();

        while(q.size() > 0) {
            st.push(q.poll());
        }
    }
}
