class Solution:
    def reverseParentheses(self, string: str) -> str:
        level = 0
        words = defaultdict(str)
        for s in string:
            if s== "(":
                level += 1
            elif s==")":
                level -= 1
                words[level] += words[level+1][::-1]
                del words[level+1]
            else:
                words[level] += s

        return words[0]
