class Solution {
public:
    string reverseParentheses(string t) {
        int n = t.length();
        string out = "";
        stack<int> s;

        for(int i=0; i<n; i++){
            if(t[i]=='(') s.push(i);
            else if(t[i]==')'){
                reverse(t.begin()+s.top()+1,t.begin()+i);
                s.pop();
            }
        }

        for(int i=0; i<n; i++) if(t[i]!='(' && t[i]!=')') out = out + t[i];
        return out;
    }
};
