string reverseParentheses(string s) {
	int l=s.length();
	if(l<=1)
		return s;
	string ans,temp;
	int i=0,par_count=0;   //index and parentheses count to avoid stack

	while(i<l){
		if(s[i]=='('){      // substring between this and it's closing bracket will be sent into recursion
			par_count=1,i++;
			temp.clear();    // temp is the substring
			while(i<l&&par_count){    // When par_count is 0 means we found the substring till the closing bracket 
				if(s[i]=='(')
					par_count++;
				if(s[i]==')')
					par_count--;
				temp+=s[i++];
			}
			temp.pop_back();          //remove closing bracket at the end
			temp= reverseParentheses(temp);      
			reverse(temp.begin(),temp.end());     // reverse the result
			ans+=temp;    
		}
		else
			ans+=s[i++];
	}
	return ans;
}
