class Solution {
public:
    string reverseParentheses(string s) {
        stack<char> org;
        stack<char> cpy;
        for(int i=0;i<s.size();i++){
            if(s[i]!=')')
                org.push(s[i]);
            else if(s[i]==')'){
                while(org.top()!='('){
                    cpy.push(org.top());
                    org.pop(); 
                }
                org.pop();
                stack<char> reverse;
                while(!cpy.empty()){
                    reverse.push(cpy.top());
                    cpy.pop();
                }
                while(!reverse.empty()){
                    org.push(reverse.top());
                    reverse.pop();
                }
            }
        }
        string rev="";
        while(!org.empty()){
            if(org.top()!='(')
                rev+=org.top();
            org.pop();
        }
        int i=0,j=rev.size()-1;
        while(i<j){
            swap(rev[i],rev[j]);
            i++;j--;
        }
        return rev;
    }
};
