class Solution {
    public String reverseParentheses(String s) {
        Stack<Integer> stack = new Stack();
        char[] arr = s.toCharArray();

        for (int i = 0; i < s.length(); i++) {
            if (arr[i] == '(') {
                stack.push(i);
            }
            if (arr[i] == ')') {
                reverse(arr, stack.pop(), i);
            }

