var reverseParentheses = function(s) {

    // 1. using stack 
    // create arrays of stack
    // for every '(' initisalise new stack
    // return/ pop() the reverse of stack on encountering ')'

    // Time complexity O(N)
    // Space complexity O(N) for stacks


    let stack = [];
    // initialise with []
    stack.push([]);

    for (let char of s) {
        if (char === '(') {
            stack.push([])
        } else if (char === ')') {
            const reverse = stack.pop().reverse();
            stack[stack.length - 1].push(...reverse);
        } else {
            stack[stack.length - 1].push(char);
        }
    }

    return stack.pop().join('');
};
