var reverseParentheses = function (s) {

  const levelsStack = [[]];
  let lastLevelIndex = 0;

  for (let i = 0; i < s.length; i++) {
    if (s[i] === '(') {
      levelsStack.push([]);
      lastLevelIndex++;
    } else if (s[i] === ')') {
      const lastLevel = levelsStack.pop();
      lastLevelIndex--;
      for (let j = lastLevel.length - 1; j >= 0; j--) {
        levelsStack[lastLevelIndex].push(lastLevel[j]);
      }
    } else {
      levelsStack[lastLevelIndex].push(s[i]);
    }
  }
  return levelsStack[lastLevelIndex].join('');
}
