var reverseParentheses = function (s) {

  function reversePart(startIndex, firstLevel) {
    let part = [];

    for (let i = startIndex; i < s.length; i++) {
      if (s[i] === '(') {
        const { partToReverse, finishedOn } = reversePart(i + 1, false);
        for (let j = partToReverse.length - 1; j >= 0; j--) {
          part.push(partToReverse[j]);
        }
        i = finishedOn;
      } else if (s[i] === ')' && !firstLevel) {
        return { part: part.join(''), finishedOn: i };
      } else {
        part.push(s[i]);
      }
    }
    return part.join('');
  }

  return reversePart(0, true);
};
