class Solution {
    public String reverseParentheses(String s) {

        Stack<Character> stack = new Stack<Character>();
        StringBuilder str = new StringBuilder(); 
        Deque<Character> d = new ArrayDeque();

        for(int i=0; i<s.length();i++){
            if(s.charAt(i) == ')'){
                //reverse the string ( a b c d

                while(!stack.isEmpty() && stack.peek() != '('){
                       d.add(stack.pop()); //d c b a                    
                }

				//remove the open bracket
                if(!stack.isEmpty()) {
                    stack.pop();
                }

                // pollFirst() method of deque remvoes the first element of deque
                while(!d.isEmpty()){
                    stack.push(d.pollFirst()); // d c b a
                }

            } else{
                stack.push(s.charAt(i)); // (,a,b,c,d,l
            }
        }

        // d c b a
        while(!stack.isEmpty()){
            str.append(stack.pop()); // a b c d
        }


        return str.reverse().toString();
    }
}
