class Solution {

    public String reverseParentheses(String s) {

        Stack<Integer> st = new Stack();    
        char[] a = s.toCharArray();
        for(int i =0;i<s.length();i++) {
            char c = a[i];
            if(c == '(') {
                st.push(i);
            } else if(c==')') {
                int index = st.pop();
                reverse(a,index,i);
            }
        }

        //reversing is done.
        //now keep only alphabets.
        StringBuilder sb = new StringBuilder();
        for(int i =0;i<s.length();i++) {
            char c = a[i];
            if(c != '('&&c!=')') {
                sb.append(c);
            } 
        }
        return sb.toString();
    }

    private void reverse (char[] a, int i, int j) {
      //two-pointers
        while(i<j) {
           while(i<a.length && (a[i]=='('||a[i]==')')) {
               i++;
           }
            while(j>=0 && (a[j]=='('||a[j]==')')) {
               j--;
           }
            if(i>j) break;

            //swap
            char temp = a[i];
            a[i]  = a[j];
            a[j]  = temp;

            i++;
            j--;
        }
    }
}

