var reverseParentheses = function(S) {
  const open = '(' , close = ')'
  const stack = [], openStack = []

  for(let i=0;i<S.length;i++){
    const s = S[i]
    if(s === open){
      openStack.push(i)
      continue
    }
    if(s === close){
      let openIdx = openStack.pop()
      let arr = S.slice(openIdx+1, i).split('')
      const newS = S.slice(0,openIdx) + arr.reverse().join('') + S.slice(i+1)
      return reverseParentheses(newS)
    }
  }
  return S
};
