            charFrequency[c] += 1

        # Create a max heap to store frequencies
        maxHeap = []

        # Push frequencies into the max heap
        for freq in charFrequency.values():
            heapq.heappush(maxHeap, -freq)  # Use negative values for max heap

        deletions = 0
