class Solution {
    public int minDeletions(String s) {
        if(s == null || s.length() == 0) {
            return 0;
        }
        int[] freq = new int[26];
        for(char ch : s.toCharArray()) {
            freq[ch - 'a']++;
        }
        PriorityQueue<Integer> pq = new PriorityQueue<>((a,b) -> b-a);
        for(int num : freq) {
            if(num>0) {
                pq.offer(num);
            }
        }
        int min = 0;
        while(!pq.isEmpty()) {
            int top = pq.poll();
            if(!pq.isEmpty() && top == pq.peek()) {
                top = top-1;
                if(top>0) {
                    pq.offer(top);
                }
                min++;
            }
        }
        return min;
    }
}
