    int minDeletions(string s) {
        unordered_map<char, int> mpp;

        for(auto alphabet : s){ // store all the distinct alphabets with their frequencies in map
            mpp[alphabet]++;
        }

        multiset<int> len;
        for(auto it : mpp){ // store all the frequencies of distinct characters in multiset
            len.insert(it.second);
        }

        int cnt = 0; // this will keep count of deleted characters

        for(auto it : mpp){ // iterate over map
            char c = it.first;
            int freq = it.second;
            if(len.count(freq) > 1){ // if frequency of this char matches with another char then we reduce it's freq
                len.erase(len.find(freq));
