class Solution {
public:
    int minDeletions(string s) {
        vector<int> freq(26); // take a frequency array
        for(auto& ch:s) freq[ch-'a']++; // find freqency of each alphabate
        sort(freq.begin(),freq.end()); // sort the freq array
