        int max=freq[25],res=0;
        for(int i=24;i>=0;i--){
            if(freq[i]==0) break;
            if(freq[i]>=max){
                int diff;
                if(max==0) diff=freq[i]; // if max is already 0, then freq[i] is gonna be 0 also
