class Solution {
    public int minDeletions(String s) {
        int[] arr = new int[26];
        for(char ch : s.toCharArray())
            arr[ch - 'a']++;
        int max = 0, count = 0;
        for(int i = 0; i < 26; i++)
            max = Math.max(max, arr[i]);
        int[] freq = new int[max + 1];
        for(int i = 0; i < 26; i++)
            freq[arr[i]]++;
        for(int i = max; i > 0; i--){
            if(freq[i] > 1){
                count += freq[i] - 1;
                freq[i - 1] += freq[i] - 1;
            }
        }
        return count;
    }
}
