class Solution:
    def minDeletions(self, s: str) -> int:
        p = [s.count(chr(97 + i)) for i in range(26)]

        p.sort(reverse=True)
        ans = 0
        for i in range(1, 26):
            if p[i - 1] == 0:
                ans += sum(p[i:])
                break
            elif p[i] >= p[i - 1]:
                ans += p[i] - p[i - 1] + 1
                p[i] = p[i - 1] - 1

        return ans
