    public int minDeletions(String s) {
        int [] counts = new int[26];

        for (char c : s.toCharArray()) {
            counts[c - 'a']++;
        }

        Arrays.sort(counts);

        int operation = 0;
        // start with the biggest frequency, try to find a "room" for every letter
        // 5->4->4->4->3->3->1
        // 5->4->3->4->3->3->1
        // 5->4->3->2->3->3->1
        // 5->4->3->2->1->3->1
        // 5->4->3->2->1->0->1
        // 5->4->3->2->1->0->0
        for (int i = counts.length - 1;  i > 0 ; i--) {
            if (counts[i - 1] >= counts[i]) {
                // spare i - 1 if it's already 0
                while (counts[i - 1] != 0 && counts[i - 1] >= counts[i]) {
                    counts[i - 1]--;
                    operation++;
                }
            }
        }

        return operation;
    }
