import java.util.Hashtable;
class Solution {

	public int minDeletions(String s) {

	   HashMap<Character,Integer> hm = new HashMap<>();
       ArrayList<Integer> arr = new ArrayList<>();

	   int i=0;
       int val;
       Hashtable<Integer,Integer> ht = new Hashtable<>();
       int count=0;        
        //create a hashmap and store :  how many times each character appeard in the given string.

        for(i=0;i<s.length();i++){
            hm.put(s.charAt(i),hm.getOrDefault(s.charAt(i),0)+1);
        }

        // iterate over the hashmap table created in above step.        
        Iterator hmIterator = hm.entrySet().iterator(); 
        i=0;

        // lets store the information: <how many times an alphabet/character appeard>
        // in an arrayList "arr"   
        while (hmIterator.hasNext()) { 
            Map.Entry mapElement = (Map.Entry)hmIterator.next(); 
            val = ((int)mapElement.getValue());            
            arr.add(val);           
            i++;        
        } 
        // sort the array
        arr.sort(Comparator.naturalOrder());     
        i = 0;        

        // now we need to find the duplicates .. for that 
        // create a hashmap table for each value in the array, 
        //representing the number of occurance of 
        // each character in the array
        // for ex: "bbcebab" , our arrylist would be : 1 1 1 4 ( as we have : a c e bbbb )
        // below logic will try to put above arraylist in a hashtable
        // while putting in hashtable it try to find if the entry already exist , if it
        // exists already then we need to decrement the counter and it is equivalent to 
        //deleting an element - as our requirement is to have each character count to be unique
        // once we decrement , then we check again if our hashtable has that entry , if not
        // then we should include that value in our hashtable 
        // ex: first we read 1 from arraylist we dont have it in HT then we insert it
        // next we read second 1 from arraylist , we see it already exist in HT 
        // so we decrement it and it becomes 0 , so we ignore as this is equal to deleting a character completely
        // next we read 1 from arraylist , we see it already existing in HT 
        // so we decrement it and it becomes 0 , so we ignore this .. we also increment "count" 
        // whenever we decrement the value of element in arraylist 
        // next we read 4 .. we dont hae 4 in our HT , so we insert it in HT . 

        for(i=0;i<arr.size();i++){                     
            if(!ht.containsKey(arr.get(i))){
                ht.put(arr.get(i),1);               
            }
            else{
                do{
                    arr.set(i,arr.get(i)-1);
                    count++;                   
                }while(ht.containsKey(arr.get(i)) && arr.get(i) > 0);    

                ht.put(arr.get(i),1);
            }            
        }
        return count;      

    }
}

